 Value can be an string for 'primaryname' ,'secondaryname' ,'text' ,'faction' ,'issuedby'  ; 
 an number for 'level', 'rarity', 'attitude', 'danger' ,'reward' ,'streetreward' ;
  an list of text for 'transgressions', 'customtransgressions'. 
 for reward or streetreward, Operator can be + | -| * | / | positive | negative | random (who take min and max value). leave it blank result as '='
